2ddca74e3d17d01ac8e69c8a2f59329e:
query TvShowSceneQuery(
  $id: ID!
) {
  tvShowDetail(id: $id) {
    ...TvDetail
    id
  }
}

fragment TvDetail on TvShow {
  isFavorite
  image {
    original
    id
  }
  ...Summary
  ...EpisodeList
  ...Cast
}

fragment Summary on TvShow {
  summary
}

fragment EpisodeList on TvShow {
  episodes {
    id
    ...EpisodeItem
  }
}

fragment Cast on TvShow {
  cast {
    id
    ...CastItem
  }
}

fragment CastItem on Cast {
  person {
    name
    image {
      medium
      id
    }
    id
  }
  character {
    name
    image {
      medium
      id
    }
    id
  }
}

fragment EpisodeItem on Episode {
  id
  seasonAndNumber
  name
  airdate
  watched
}

