query SearchContainerQuery(
  $query: String!
) {
  searchTvShow(query: $query) {
    ...TvShowList
  }
}

fragment TvShowList on TvShowConnection {
  edges {
    node {
      id
      ...TvShowItem
    }
  }
}

fragment TvShowItem on TvShow {
  id
  name
  status
  rating
  image {
    medium
    id
  }
}

