mutation ToggleFavoriteMutation(
  $serieId: ID!
  $add: Boolean!
) {
  toggleFavorite(serieId: $serieId, add: $add) {
    success
    serieId
    tvShow {
      node {
        id
        name
        image {
          original
          medium
          id
        }
        previousEpisode
        nextEpisode
      }
    }
  }
}

