query FavoritesSceneQuery(
  $first: Int
  $options: SortOptions
) {
  favorites(first: $first, options: $options) {
    edges {
      ...FavoritesList
      cursor
      node {
        __typename
        id
      }
    }
    pageInfo {
      endCursor
      hasNextPage
    }
  }
}

fragment FavoritesList on TvShowEdge {
  node {
    id
    ...FavoritesItem
  }
}

fragment FavoritesItem on TvShow {
  id
  name
  image {
    medium
    id
  }
  previousEpisode
  nextEpisode
  status
}

